import base64


def save_base64_image(base64_string, output_path):
    """
    Save the base64 encoded string as an image file.

    Parameters:
    - base64_string: base64 encoded string.
    - output_path: Path to save the image.
    """

    # Decode the base64 encoded string.
    decoded_data = base64.b64decode(base64_string)

    # Save the decoded data as an image file.
    with open(output_path, "wb") as image_file:
        image_file.write(decoded_data)


# Example: the image of the template author.
base64_string = ""
save_base64_image(base64_string, "decoded_image.webp")
